mapper.select:
SELECT COUNT(*)
  FROM MEETING
 WHERE USER_NO = #{user_no}

SELECT MAX(MEETING_NO)
  FROM MEETING
 WHERE USER_NO = #{user_no}

SELECT *
  FROM MEETING
 WHERE USER_NO = #{param1}
   AND MEETING_NO != #{param2}

SELECT M.USER_NO, U.USER_NICKNAME, U.USER_MESSAGE, M.MEETING_NO, M.MEETING_TITLE,
	   M.MEETING_DATE, M.EXERCISE_NO, M.CREATED_AT, M.meeting_max, M.meeting_min
  FROM USERS U FULL OUTER JOIN MEETING M
    ON M.USER_NO = U.USER_NO
 WHERE M.USER_NO != #{param1}
   AND M.EXERCISE_NO = #{param2}
